import React, {Component, useEffect, useState} from 'react';
import './Components/styles/App.css';
import {NavLink} from 'react-router-dom';
import SwitchRoute from './SwitchRoute';
import background from './Components/images/daytoner2.jpg';

const isPhantomInstalled = window.solana && window.solana.isPhantom

const App = () => {
	const [walletAddress, setWalletAddress] = useState(null);
	const checkIfWalletIsConnected = async () => {
		try {
			const { solana } = window;

			if (solana) {
				if (solana.isPhantom) {
					console.log('Phantom wallet found!');
					const response = await solana.connect({ onlyIfTrusted: true });
					console.log('Connected with Public Key:',response.publicKey.toString());
        
					setWalletAddress(response.publicKey.toString());
					}
				} else {
					alert('Solana object not found! Get a Phantom Wallet');
				}
			} catch (error) {
				console.error(error);
			}
		};

	const connectWallet = async () => {
		const { solana } = window;

		if (solana) {
			const response = await solana.connect();
			console.log('Connected with Public Key:', response.publicKey.toString());
			setWalletAddress(response.publicKey.toString());
		}
	};
	const renderNotConnectedContainer = () => (
		<div>
			<button class="connectbtn animated" onClick={connectWallet}>CONNECT</button>
			<div>Connect to Phantom Wallet to log in your account.</div>
		</div>
	 );
	 const renderConnectedUser = () => (
	     <div>
			<div className="userName">
				<div className="userDP"></div> Username01
			</div>
			<div className="userCurrency">
				0.00 SOL
			</div>
			<div className="userSocial">
				Socials
			</div>
			<div className="friendName">
				<div className="friendDP"></div> ExampleFriend01
			</div>
			<div className="friendName">
				<div className="friendDP"></div> ExampleFriend02
			</div>
			<div className="friendName">
				<div className="friendDP"></div> ExampleFriend03
			</div>
			<div className="friendName">
				<div className="friendDP"></div> ExampleFriend04
			</div>
		 </div>
	 );

	useEffect(() => {
		const onLoad = async () => {
			  await checkIfWalletIsConnected();
		};
		window.addEventListener('load', onLoad);
		return () => window.removeEventListener('load', onLoad);
	}, []);
		return (
			<div className="background" style={{ backgroundImage: `url(${background})` }}>
				<div className="fadetop">
					<div className = "App">
						<NavLink className="nav-link animated" to="/">
						HOME
						</NavLink>
						<NavLink className="nav-link nav-link-active" to="/marketplace">
						MARKETPLACE
						</NavLink>
						<NavLink className="nav-link animated" to="/leaderboards">
						LEADERBOARDS
						</NavLink>
						<NavLink className="nav-link animated" to="/inventory">
						INVENTORY
						</NavLink>
						<NavLink className="nav-link animated" to="/profile">
						PROFILE
						</NavLink>
						<div style={{fontSize: '3em', margin: '25px'}}>
							<SwitchRoute/>
						</div>
					</div>
				</div>
				<div className="sideBar">
					{!walletAddress && renderNotConnectedContainer()}
					{walletAddress && renderConnectedUser()}
				</div>
				
			</div>
		);
}

export default App;
