import React, {Component} from 'react';
import './App.css';
import {NavLink} from 'react-router-dom';
import SwitchRoute from './SwitchRoute';
import background from './Components/images/daytoner2.jpg';


class App extends Component {
	render() {
		const active = {textDecoration: 'none', cursor: 'default', color: 'grey'};
		const defaultStyle = {margin:'5px'}
		return (
			<div className="background" style={{ backgroundImage: `url(${background})` }}>
				<div className="fadetop">
					<div className = "App">
						<NavLink exact style={defaultStyle} activeStyle={active} to="/">
						HOME
						</NavLink>
						<NavLink exact style={defaultStyle} to="/marketplace">
						MARKETPLACE
						</NavLink>
						<NavLink exact style={defaultStyle} activeStyle={active} to="/account">
						ACCOUNT
						</NavLink>
						<div style={{fontSize: '3em', margin: '25px'}}>
							<SwitchRoute/>
						</div>
					</div>
				</div>
				
			</div>
		);
	}
}

export default App;
