import React, {Component} from 'react';
import './styles/Marketplace.css';
import Chart from 'chart.js/auto';


class Marketplace extends Component {
	constructor(props){
		super(props);
		this.state = {
		showPage: false,
		item: []
		}
	}

	componentDidlMount(){
		fetch('/api/item')
		.then(data => data.json())
		.then(lot => this.setState({lot}));
	}
	
	render(){
		const showModal = () => this.setState({showPage:true});
		const hideModal = () => this.setState({showPage:false});
		const ctx = document.getElementById('myChart');
		const myChart = new Chart(ctx, {
			type: 'bar',
			data: {
				labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
				datasets: [{
					label: '# of Votes',
					data: [12, 19, 3, 5, 2, 3],
					backgroundColor: [
						'rgba(255, 99, 132, 0.2)',
						'rgba(54, 162, 235, 0.2)',
						'rgba(255, 206, 86, 0.2)',
						'rgba(75, 192, 192, 0.2)',
						'rgba(153, 102, 255, 0.2)',
						'rgba(255, 159, 64, 0.2)'
					],
					borderColor: [
						'rgba(255, 99, 132, 1)',
						'rgba(54, 162, 235, 1)',
						'rgba(255, 206, 86, 1)',
						'rgba(75, 192, 192, 1)',
						'rgba(153, 102, 255, 1)',
						'rgba(255, 159, 64, 1)'
					],
					borderWidth: 1
				}]
			},
			options: {
				scales: {
					y: {
						beginAtZero: true
					}
				}
			}
		});
		const nftPage = () => (
			<div>
				<div className="modalbg" onClick={hideModal}>
				</div>
				<div className="modalFighter">
					<div className="charlieFull"></div>
					<div className="nftDetails">
						<label className="nftName">CHARLIE</label>
						<div className="nftDescription">
							Created for the Omeda City military, Crunch was sent to the junkyard when newer biotech androids took his place. 
							After months in rest mode, Crunch followed the sounds of battle to an underground fighting ring, started punching, and became an immediate hit with the crowd.
						</div>
						<div className="nftDescription">
							<div className="flexItem">Total Supply: 500</div>
							<div className="flexItem">Available Market Supply: 500/500</div>
						</div>
					</div>
				</div>
			</div>
		 );
		return(
			<div>
				<div className="filterbar">
					<div className="arrow">></div>
					Filter Marketplace:
					<input className="searchbar" type="text" placeholder="Search"></input>
					<div className="sort">
						<select name="sortSelect">
							<option value="lowestprice">Lowest Price</option>
							<option value="highestprice">Highest Price</option>
							<option value="latest">Latest</option>
							<option value="oldest">Oldest</option>
						</select>
					</div>
					<div className="searchbar">
					</div>

				</div>
				<div className="container">
					<div className="storeItem" onClick={showModal}>
						<div className="storeImage alpha">
						</div>
						<div className="storeTextContainer">
							<h3>ALPHA<span className="storeItemPrice">0.05 SOL</span></h3>
							Example NFT Game Character that can be bought for auction sold by users.
						</div>
					</div>
					<div className="storeItem" onClick={showModal}>
						<div className="storeImage bravo">
						</div>
						<div className="storeTextContainer">
							<h3>BRAVO<span className="storeItemPrice">0.05 SOL</span></h3>
							Example NFT Game Character that can be bought for auction sold by users.
						</div>
					</div>
					<div className="storeItem" onClick={showModal}>
						<div className="storeImage charlie">
						</div>
						<div className="storeTextContainer">
							<h3>CHARLIE<span className="storeItemPrice">0.05 SOL</span></h3>
							Example NFT Game Character that can be bought for auction sold by users.
						</div>
					</div>
					<div className="storeItem" onClick={showModal}>
						<div className="storeImage delta">
						</div>
						<div className="storeTextContainer">
							<h3>DELTA<span className="storeItemPrice">0.05 SOL</span></h3>
							Example NFT Game Character that can be bought for auction sold by users.
						</div>
					</div>
						<canvas id="myChart" width="400" height="400"></canvas>
				</div>
				{this.state.showPage && nftPage()}
			</div>
		)
	}
}
export default Marketplace;
