import React, {Component, useEffect, useState} from 'react';
import './App.css';
import {NavLink} from 'react-router-dom';
import SwitchRoute from './SwitchRoute';
import background from './Components/images/daytoner2.jpg';

const isPhantomInstalled = window.solana && window.solana.isPhantom

const App = () => {
	const [walletAddress, setWalletAddress] = useState(null);
	const checkIfWalletIsConnected = async () => {
		try {
		  const { solana } = window;

		  if (solana) {
			if (solana.isPhantom) {
			  console.log('Phantom wallet found!');
			  const response = await solana.connect({ onlyIfTrusted: true });
			  console.log(
				'Connected with Public Key:',
				response.publicKey.toString()
			  );
			}
		  } else {
			alert('Solana object not found! Get a Phantom Wallet');
		  }
		} catch (error) {
		  console.error(error);
		}
	 };

	 const connectWallet = async () => {};
	 const renderNotConnectedContainer = () => (
		<button
		  className="cta-button connect-wallet-button"
		  onClick={connectWallet}
		>
		  Connect to Wallet
		</button>
	  );

	useEffect(() => {
		const onLoad = async () => {
			  await checkIfWalletIsConnected();
		};
		window.addEventListener('load', onLoad);
		return () => window.removeEventListener('load', onLoad);
	}, []);
		return (
			<div className="background" style={{ backgroundImage: `url(${background})` }}>
				<div className="fadetop">
					<div className = "App">
						<NavLink className="nav-link" to="/">
						HOME
						</NavLink>
						<NavLink className="nav-link nav-link-active" to="/marketplace">
						MARKETPLACE
						</NavLink>
						<NavLink className="nav-link" to="/leaderboards">
						LEADERBOARDS
						</NavLink>
						<NavLink className="nav-link" to="/inventory">
						INVENTORY
						</NavLink>
						<NavLink className="nav-link" to="/profile">
						PROFILE
						</NavLink>
						<div style={{fontSize: '3em', margin: '25px'}}>
							<SwitchRoute/>
						</div>
					</div>
				</div>
				<div className="sideBar">
					<button class="connectbtn">CONNECT</button>
					<div>
						Connect to Phantom Wallet to log in your account.
					</div>

				</div>
				
			</div>
		);
}

export default App;
